svg.script:
const border1 = document.querySelector('#border1');
const border2 = document.querySelector('#border2');
const border3 = document.querySelector('#border3');
document.transformFunction = {
    toCloseButton: function(){
        border1.classList.add('close-border1');
        border2.classList.add('close-border2');
        border3.classList.add('close-border3');
    },
    toHamburgerButton: function() {
        border1.classList.remove('close-border1');
        border2.classList.remove('close-border2');
        border3.classList.remove('close-border3');
    },
    getIconStatus: function() {
        if (border1.classList.contains('close-border1')) {
            return 'closeButton';
        } else {
            return 'hamburgerButton';
        }
    }
}
